package com.syntax.class01;

public class PracticeVariables {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		byte var1 =100;
		short var2 = 10000;
		int var3 = 1000000000;
		long var4 = 999999999999L;
		float var5 = 10.99999f;
		double var6 = 10.9999999999999999;
		char singleLetter ='b';
		char specialCharacter = '$';
		boolean myBoo = true;
		boolean myBoo2 = false;
		
		System.out.println (var1);
		System.out.println (var2);
		System.out.println (var3);
		System.out.println(var4);
		System.out.println(var5);
		System.out.println(var6);
		System.out.println(singleLetter);
		System.out.println(specialCharacter);
		System.out.println(myBoo);
		System.out.println(myBoo2);
		
	}

}
